import './App.css';
import React, { useEffect, useState } from 'react';

function App() {

  const [UIschema, setUIschema] = useState(null);
  const [components, setComponents] = useState([]);

  useEffect(() => {
    // fetch UI schema
    fetch('http://127.0.0.1:5000/schema/')
      .then(response => response.json())
      .then(json => setUIschema(json));

    // fetch data (products, customers, orders)
    fetch('http://127.0.0.1:5000/data/')
      .then(response => response.json())
      .then(json => {
        setComponents(json.components);
      })
      .catch(err => console.error('Error fetching data', err));
  }, []);

  // render page layout
  const renderPage = (page) => {
    const ly = page.layout;
    return (
      <div style={{ 
        height: '100vh', 
        display: ly.display, 
        gridTemplateRows: ly.gridRows, 
        gridTemplateColumns: ly.gridColumns, 
        gridTemplateAreas: ly.gridAreas.join(' ')
        }}>
        {ly.components.map((component, index) => {
          if (component.layout === 'row') {
            console.log('background color', component);
            return <nav key={index} style={{ backgroundColor: component.color, gridArea: component.gridArea}}>{component.title}</nav>
          }
          else if (component.layout === 'grid'){
            return <aside key={index} style={{ backgroundColor: component.color, gridArea: component.gridArea}}>Aside</aside>
          }
          else if (component.layout === 'column'){
            return <main key={index} style={{ backgroundColor: component.color, gridArea: component.gridArea}}>Middle</main>
          }
          else {
            return <div key={index}>Unknown layout</div>
          }
        })}
      </div>
    )

  }

  // // render individual UI component 
  // const renderUIComponent = (UIcomponent) => {
  // }

  // flips only selected value -> returns updated product as JSON
  const productClicked = (productId) => {
    fetch(`http://127.0.0.1:5000/data/products/${productId}`, {
      method: 'PATCH',
      headers: {'Content-Type': 'application/json'}, 
      mode: 'cors'}) // PATCH: partially update a resource
      .then(response => response.json())
      .then(updatedProduct => { // receive updated product in React
        console.log('product updated:', updatedProduct);
        // updates component object, React re-renders
        setComponents(prev => 
          prev.map(component =>
            component.component === 'products' ? {
              ...component, data: component.data.map(p =>
                p.id === updatedProduct.id ? updatedProduct : p
              )
            } : component
          )
        );
      })
      .catch(err => console.error('Error updating product', err));
  }

  return (
    <div>
      <div>{UIschema? renderPage(UIschema.pages[0]) : <p>Loading...</p>}</div>
    </div>
    // <div>
    //   {components
    //     .filter(component => component.component === 'products')
    //     .map((component, index) => (
    //       <div key={index} style={{ display: 'flex', flexWrap: 'wrap'}}>
    //         {component.data.map((item, i) => {
    //           const hasBoolean = Object.values(item).some(value => typeof value === 'boolean');

    //           return (
    //             <div key={i}>
    //               {/* If one of the fields has a boolean data type, it automatically renders to a box */}
    //               {hasBoolean && (
    //                 <div style={{ backgroundColor: item.selected ? '#c5e0d8' : '#e1edea', width: '250px', height: '150px', margin: '20px', padding: '5px', textAlign: 'center', borderRadius: '5px', cursor: 'pointer'}}
    //                   onClick={() => productClicked(item.id)}
    //                 >
    //                   <p style={{ paddingTop: 'px'}}>{item.name}</p>
    //                   <p>{item.price}</p>
    //                 </div>
    //               )}
    //             </div>
    //           )
    //         })}
    //       </div>
    //     ))
    //   }
    // </div>
  );
}

export default App;
