import './App.css';
import React, { useEffect, useState } from 'react';

function App() {

  const [components, setComponents] = useState([]);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/data/')
      .then(response => response.json())
      .then(json => {
        console.log(json);
        setComponents(json.components);
      })
      .catch(err => console.error('Error fetching data', err));
  }, []);

  return (
    <div>
      <h1>E-commerce database</h1>
      {components
        .filter(component => component.component === 'products')
        .map((component, index) => (
          <div key={index}>
            {component.data.map((item, i) => {
              const hasBoolean = component.data.some(item =>
                Object.values(item).some(value => typeof value === 'boolean')
              );
            })}
            return (
              <div key={i}>
                {hasBoolField && (
                  <div className='productBox' style={{ backgroundColor: 'grey', width: '100px', height: '80px', margin: '20px'}}></div>
                )}
                <pre>{JSON.stringify(component.data, null, 2)}</pre>
              </div>
            )
          </div>
        ))
      }
    </div>
  );
}

export default App;
