import './App.css';
import React, { useEffect, useState } from 'react';

function App() {

  const [components, setComponents] = useState([]);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/data/')
      .then(response => response.json())
      .then(json => {
        console.log(json);
        setComponents(json.components);
      })
      .catch(err => console.error('Error fetching data', err));
  }, []);

  return (
    <div>
      <h1>E-commerce database</h1>
      {components
        .filter(component => component.component === 'products')
        .map((component, index) => (
          <div key={index} style={{ display: 'flex', flexWrap: 'wrap'}}>
            {component.data.map((item, i) => {
              const hasBoolean = Object.values(item).some(value => typeof value === 'boolean');

              return (
                <div key={i}>
                  {hasBoolean && (
                    <div style={{ backgroundColor: 'grey', width: '200px', height: '100px', margin: '20px', padding: '5px', textAlign: 'center', borderRadius: '4px'}}>
                      <p style={{ paddingTop: 'px'}}>{item.name}</p>
                      <p>{item.price}</p>
                    </div>
                  )}
                </div>
              )
            })}
          </div>
        ))
      }
    </div>
  );
}

export default App;
